import express from 'express';
import path from 'path';
import { fileURLToPath } from 'url';

// Recreate __dirname equivalent for ES Modules
const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

const app = express();
const port = process.env.PORT || 3000;

// Serve static files from the 'dist' directory (generated by vite build)
app.use(express.static(path.join(__dirname, 'dist')));

// Catch-all route: Redirect any non-static file request to index.html
// This is essential for React Router (Single Page Application)
app.get('*', (req, res) => {
  res.sendFile(path.join(__dirname, 'dist', 'index.html'));
});

app.listen(port, () => {
  console.log(`Server running on port ${port}`);
});
